simulation.module:
def configure(config):
	print "Color:", config.get("color")

def update(dt, simulation):
	print "Iteration:", simulation.iteration, "/", simulation.iterations
	print "Object count:", simulation.objectsCount
	print "Size: [", simulation.worldSize.width, ",", simulation.worldSize.height, "]"

simulation.program:
def call(self, dt):
	pos = self.position
	print "Object ID: ", self.id, "[", pos.x, ",", pos.y, "]"
