file = open("sweden.csv", 'wb')
file.write("iteration;totalTime;id");

for code in range(ord('A'), ord('K')):
     file.write(";" + chr(code))

file.write("\n")

# Program code
def call(object, simulation, dt):
    file.write(str(simulation.iteration) + ";" + str(simulation.totalTime) + ";" + str(object.id));

    for code in range(ord('A'), ord('K')):
         file.write(";" + str(object.moleculeCount(chr(code))))

    file.write("\n")
    file.flush()
